import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';


class CurrencyInput extends React.Component{
  constructor(props){
    super(props);           //??
    this.handleChange = this.handleChange.bind(this);  //Handle change: Änderungen verarbeiten
  }
  handleChange(e) {
    this.props.onCurrencyChange(e.target.value);
  }
  render(){
    const currency = this.props.currency ;
    const value = this.props.value;
    return (
      <fieldset>
        <legend>Gib die Menge in {currency} an:</legend>
        <input value={value} onChange={this.handleChange}/> 
      </fieldset>
    ) //jedes mal, wo der currency calculator im div ein Currencyinput rendert, werden die props weitergegeben und für jeden currency wird ein FEld mit input gezeichnet
  }
}

class CurrencyCalculator extends React.Component {
  constructor(props){
    super(props);
    this.state = {currency: 'Euro', value: ''};
    this.handleEuroChange = this.handleEuroChange.bind(this);
    this.handleDollarChange = this.handleDollarChange.bind(this);
  }

  handleDollarChange(value) {
    this.setState({currency: 'Dollar', value});
  }

  handleEuroChange(value) {
    this.setState({currency: 'Euro', value});
  }


  render(){
    const value = this.state.value;
    const currency = this.state.currency;
    let euro = 0;
    let dollar = 0;
    if(currency === "Euro"){
      euro = value;
      dollar = 1.13*value;
    }
    else{
      dollar = value;
      euro = 0.88*value;
    }
    return (
      <div>
      <CurrencyInput currency="Euro" value={euro} onCurrencyChange={this.handleEuroChange}/>
      <CurrencyInput currency="Dollar" value={dollar} onCurrencyChange={this.handleDollarChange}/>
      </div>
    );
  }
}


ReactDOM.render(
  <App/>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
